    private void btnCadastrarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnCadastrarActionPerformed
        Filmes f = new Filmes();
        
        try {
            f.setNome(fieldNomeFilme.getText());
            f.setDataDeLancamento(Filmes.converterParaSQL(fieldDataFilme.getText()));
            f.setCategoria(fieldCategoria.getText());
            
            
            FilmesDao.cadastrar (f);
            
        }catch(SQLException ex) {
            JOptionPane.showMessageDialog(null, "O filme nao pode ser cadastrado! Tente novamente!");
            System.out.println(ex);
        }
       
    }//GEN-LAST:event_btnCadastrarActionPerformed

